import React, { useState, useEffect, useRef } from 'react';
import NavBarCommon from '../../components/NavBarCommon';
import Stack from 'react-bootstrap/Stack';
import './Dashboard.css';
import { Container } from 'react-bootstrap';
import FooterCommon from '../../components/FooterCommon';
import * as faceapi from 'face-api.js';

export default function Dashboard() {
  const [distance, setDistance] = useState(0);
  const videoRef = useRef(null);

  useEffect(() => {
    Promise.all([
      faceapi.nets.tinyFaceDetector.loadFromUri('/models'),
      faceapi.nets.faceLandmark68Net.loadFromUri('/models'),
      faceapi.nets.faceRecognitionNet.loadFromUri('/models'),
      faceapi.nets.faceExpressionNet.loadFromUri('/models')
    ]).then(() => {
      startVideo();
    });
  }, []);

  const startVideo = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;
      videoRef.current.play();

      const canvas = faceapi.createCanvasFromMedia(videoRef.current);
      document.body.append(canvas);

      const displaySize = {
        width: videoRef.current.width,
        height: videoRef.current.height
      };
      faceapi.matchDimensions(canvas, displaySize);

      setInterval(async () => {
        const detections = await faceapi.detectAllFaces(videoRef.current, new faceapi.TinyFaceDetectorOptions())
          .withFaceLandmarks()
          .withFaceExpressions();
        const resizedDetections = faceapi.resizeResults(detections, displaySize);
        canvas.getContext('2d').clearRect(0, 0, canvas.width, canvas.height);

        if (resizedDetections.length > 0) {
          const newDistance = calculateDistance(resizedDetections);
          setDistance(newDistance);
        }

        faceapi.draw.drawFaceLandmarks(canvas, resizedDetections);
        showDistanceOnCanvas(canvas, distance, { x: 10, y: 30 });
      }, 100);
    } catch (error) {
      console.error('Error accessing camera:', error);
    }
  };

  return (
    <div>
      <NavBarCommon />
      <Container className='dashboardDivCardsPanel'>
        <Stack className="dashboardCardsPanel h-100" gap={3}>
          {/* ... other rows ... */}
          <Stack direction="horizontal" className='dashboardSecondRow' gap={3}>
            {/* ... other cards ... */}
            <video ref={videoRef} id="video" autoplay muted className="p-2 card dashboardCards">Camera</video>
          </Stack>
          {/* ... other rows ... */}
        </Stack>
      </Container>
      <FooterCommon />
    </div>
  );
}
