import React, { useState, useEffect } from 'react'
import NavBarCommon from '../../components/NavBarCommon'
import Accordion from 'react-bootstrap/Accordion';
import Container from 'react-bootstrap/Container';
import './Profile.css';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import FooterCommon from '../../components/FooterCommon'
import { useNavigate } from "react-router-dom";
import PostService from "../../services/post.service";
import AuthService from "../../services/auth.service";

export default function Profile() {
  const [name, setName] = useState([""]);
  const [username, setUsername] = useState([""]);
  const [email, setEmail] = useState([""]);
  const [contact, setContact] = useState([""]);
  const [birthday, setBirthday] = useState([""]);
  const [password, setPassword] = useState([""]);

  const navigate = useNavigate();

  useEffect(() => {
    if (AuthService.getCurrentUser() == null) {
      navigate("/login");
    } else {
      const currentUser = AuthService.getCurrentUser().username;
      PostService.getUserDetails(currentUser)
        .then((response) => {
          console.log(response);
          setName(response.data.name);
          setUsername(response.data.username);
          setEmail(response.data.email);
          setBirthday(response.data.birthday);
          setContact(response.data.contactNo);
        })
        .catch((error) => {
          console.log("Private page", error.response);
          if (error.response && error.response.status === 403) {
            AuthService.logout();
            navigate("/login");
            window.location.reload();
          }
        });
    }
  }, [navigate]);

  const handleChangeName = async (e) => {
    e.preventDefault();
    try {
      // await authService.login(username, password).then(
      //   () => {
      //     Navigate("/dashboard");
      //     window.location.reload();
      //   },
      //   (error) => {
      //     alert(error.response.data.message)
      //   }
      // );
    } catch (err) {
      console.log(err);
    }
  };



  return (
    <div>
      <NavBarCommon />
      <Container className='profileAccordionContainer'>
        <Accordion className='profileAccordion'>
          <h1>Profile</h1>
          <Accordion.Item eventKey="0">
            <Accordion.Header>Name: {name}</Accordion.Header>
            <Accordion.Body>
              <Form>
                <Row className="align-items-center">
                  <Col sm={3} className="my-1">
                    <Form.Control id="inlineFormInputName" placeholder="New name" />
                  </Col>
                  <Col xs="auto" className="my-1">
                    <Button type="submit">Change</Button>
                  </Col>
                </Row>
              </Form>
            </Accordion.Body>
          </Accordion.Item>
          <Accordion.Item eventKey="1">
            <Accordion.Header>Username: {username}</Accordion.Header>
            <Accordion.Body>
              <Form>
                <Row className="align-items-center">
                  <Col sm={3} className="my-1">
                    <Form.Control id="inlineFormInputUsername" placeholder="New username" />
                  </Col>
                  <Col xs="auto" className="my-1">
                    <Button type="submit">Change</Button>
                  </Col>
                </Row>
              </Form>
            </Accordion.Body>
          </Accordion.Item>
          <Accordion.Item eventKey="2">
            <Accordion.Header>Email: {email}</Accordion.Header>
            <Accordion.Body>
              <Form>
                <Row className="align-items-center">
                  <Col sm={3} className="my-1">
                    <Form.Control id="inlineFormInputEmail" placeholder="New email" />
                  </Col>
                  <Col xs="auto" className="my-1">
                    <Button type="submit">Change</Button>
                  </Col>
                </Row>
              </Form>
            </Accordion.Body>
          </Accordion.Item>
          <Accordion.Item eventKey="3">
            <Accordion.Header>Contact No: {contact}</Accordion.Header>
            <Accordion.Body>
              <Form>
                <Row className="align-items-center">
                  <Col sm={3} className="my-1">
                    <Form.Control id="inlineFormInputContact" placeholder="New contact number" />
                  </Col>
                  <Col xs="auto" className="my-1">
                    <Button type="submit">Change</Button>
                  </Col>
                </Row>
              </Form>
            </Accordion.Body>
          </Accordion.Item>
          <Accordion.Item eventKey="4">
            <Accordion.Header>Birthday: {birthday}</Accordion.Header>
            <Accordion.Body>
              <Form>
                <Row className="align-items-center">
                  <Col sm={3} className="my-1">
                    <Form.Control id="inlineFormInputBirthday" placeholder="New birthday" />
                  </Col>
                  <Col xs="auto" className="my-1">
                    <Button type="submit">Change</Button>
                  </Col>
                </Row>
              </Form>
            </Accordion.Body>
          </Accordion.Item>
          <Accordion.Item eventKey="5">
            <Accordion.Header>Password</Accordion.Header>
            <Accordion.Body>
              <Form>
                <Row className="align-items-center">
                  <Col sm={3} className="my-1">
                    <Form.Control id="inlineFormInputPassword" placeholder="New password" />
                  </Col>
                  <Col xs="auto" className="my-1">
                    <Button type="submit">Change</Button>
                  </Col>
                </Row>
              </Form>
            </Accordion.Body>
          </Accordion.Item>
        </Accordion>
      </Container>
      <FooterCommon />
    </div>
  )
}
